INSERT INTO preguntas (id_test, tipo_pregunta, nombre_pregunta, datos_pregunta, retroalimentacion) VALUES
(141, 2, '¿Cuáles de los siguientes filósofos pertenecieron a la corriente del existencialismo?', '{"respuestas": ["Søren Kierkegaard", "Friedrich Nietzsche", "Immanuel Kant", "John Locke"], "respuestas_correctas": ["Søren Kierkegaard", "Friedrich Nietzsche"]}', 'Los filósofos que pertenecieron a la corriente del existencialismo son Søren Kierkegaard y Friedrich Nietzsche.'),
(141, 2, '¿Cuáles de las siguientes corrientes filosóficas se enfocan en el estudio de la ética?', '{"respuestas": ["Utilitarismo", "Escepticismo", "Positivismo lógico", "Materialismo dialéctico"], "respuestas_correctas": ["Utilitarismo", "Escepticismo"]}', 'Las corrientes filosóficas que se enfocan en el estudio de la ética son el Utilitarismo y el Escepticismo.'),
(141, 2, '¿Cuáles de las siguientes corrientes filosóficas se enfocan en el estudio de la realidad y la percepción?', '{"respuestas": ["Idealismo", "Existencialismo", "Pragmatismo", "Escepticismo"], "respuestas_correctas": ["Idealismo", "Escepticismo"]}', 'Las corrientes filosóficas que se enfocan en el estudio de la realidad y la percepción son el Idealismo y el Escepticismo.'),
(141, 2, '¿Cuáles de los siguientes filósofos son considerados precursores del pensamiento político?', '{"respuestas": ["Platón", "Aristóteles", "Sócrates", "Epicuro"], "respuestas_correctas": ["Platón", "Aristóteles", "Sócrates"]}', 'Los filósofos considerados precursores del pensamiento político son Platón, Aristóteles y Sócrates.'),
(141, 2, '¿Cuáles de los siguientes filósofos son conocidos por sus contribuciones al pensamiento político?', '{"respuestas": ["Thomas Hobbes", "John Locke", "Jean-Jacques Rousseau", "Immanuel Kant"], "respuestas_correctas": ["Thomas Hobbes", "John Locke", "Jean-Jacques Rousseau"]}', 'Los filósofos conocidos por sus contribuciones al pensamiento político son Thomas Hobbes, John Locke y Jean-Jacques Rousseau.'),
(141, 2, '¿Cuáles de los siguientes filósofos son considerados representantes del pensamiento nihilista?', '{"respuestas": ["Friedrich Nietzsche", "Arthur Schopenhauer", "Søren Kierkegaard", "Jean-Paul Sartre"], "respuestas_correctas": ["Friedrich Nietzsche", "Arthur Schopenhauer"]}', 'Los filósofos considerados representantes del pensamiento nihilista son Friedrich Nietzsche y Arthur Schopenhauer.'),
(141, 2, '¿Cuáles de los siguientes filósofos son conocidos por sus contribuciones al pensamiento feminista?', '{"respuestas": ["Simone de Beauvoir", "Judith Butler", "John Locke", "René Descartes"], "respuestas_correctas": ["Simone de Beauvoir", "Judith Butler"]}', 'Los filósofos conocidos por sus contribuciones al pensamiento feminista son Simone de Beauvoir y Judith Butler.'),
(141, 2, '¿Cuáles de las siguientes corrientes filosóficas se enfocan en el estudio de la verdad y el conocimiento?', '{"respuestas": ["Racionalismo", "Pragmatismo", "Materialismo dialéctico", "Escepticismo"], "respuestas_correctas": ["Racionalismo", "Escepticismo"]}', 'Las corrientes filosóficas que se enfocan en el estudio de la verdad y el conocimiento son el Racionalismo y el Escepticismo.'),
