INSERT INTO preguntas (id_test, tipo_pregunta, nombre_pregunta, datos_pregunta, retroalimentacion) VALUES
(213, 1, '¿Qué es la Ley de Ohm?', '{"respuestas": ["Relación entre la corriente eléctrica, la resistencia y la diferencia de potencial", "Relación entre la masa y la aceleración de un objeto", "Relación entre la fuerza y la aceleración de un objeto", "Relación entre la presión y el volumen de un gas"], "respuesta_correcta": "Relación entre la corriente eléctrica, la resistencia y la diferencia de potencial"}', null),
(213, 1, '¿Qué es la Ley de la Gravitación Universal?', '{"respuestas": ["Ley que describe la fuerza de atracción entre dos objetos debido a su masa", "Ley que describe el desplazamiento de un objeto en función del tiempo", "Ley que describe la conservación de la energía mecánica", "Ley que describe la relación entre la presión y el volumen de un gas"], "respuesta_correcta": "Ley que describe la fuerza de atracción entre dos objetos debido a su masa"}', null),
(213, 1, '¿Qué es el Principio de Conservación de la Energía?', '{"respuestas": ["La energía total de un sistema aislado se mantiene constante", "La energía cinética de un objeto es proporcional a su masa y velocidad", "La energía potencial elástica se almacena en objetos deformables", "La energía térmica se transfiere de un objeto a otro debido a una diferencia de temperatura"], "respuesta_correcta": "La energía total de un sistema aislado se mantiene constante"}', null),
(213, 1, '¿Qué es la Ley de Conservación del Momento Lineal?', '{"respuestas": ["El momento lineal total de un sistema aislado se mantiene constante", "La aceleración de un objeto es directamente proporcional a la fuerza neta aplicada sobre él", "La fuerza de fricción es proporcional a la normal entre dos superficies en contacto", "La resistencia eléctrica es inversamente proporcional a la corriente eléctrica"], "respuesta_correcta": "El momento lineal total de un sistema aislado se mantiene constante"}', null),
(213, 1, '¿Qué es el Principio de Arquímedes?', '{"respuestas": ["Un objeto sumergido en un fluido experimenta una fuerza hacia arriba igual al peso del fluido desplazado", "La fuerza ejercida por un objeto es igual y opuesta a la fuerza ejercida sobre él", "La aceleración de un objeto es directamente proporcional a la fuerza neta aplicada sobre él", "La resistencia eléctrica es inversamente proporcional a la corriente eléctrica"], "respuesta_correcta": "Un objeto sumergido en un fluido experimenta una fuerza hacia arriba igual al peso del fluido desplazado"}', null),
(213, 1, '¿Qué es la Ley de Newton de la Gravitación Universal?', '{"respuestas": ["Ley que describe la fuerza de atracción entre dos objetos debido a su masa", "Ley que describe el desplazamiento de un objeto en función del tiempo", "Ley que describe la conservación de la energía mecánica", "Ley que describe la relación entre la presión y el volumen de un gas"], "respuesta_correcta": "Ley que describe la fuerza de atracción entre dos objetos debido a su masa"}', null),
(213, 1, '¿Qué es la Ley de Coulomb?', '{"respuestas": ["Ley que describe la fuerza eléctrica entre dos cargas", "Ley que describe la relación entre la presión y el volumen de un gas", "Ley que describe el desplazamiento de un objeto en función del tiempo", "Ley que describe la conservación de la energía mecánica"], "respuesta_correcta": "Ley que describe la fuerza eléctrica entre dos cargas"}', null),
(213, 1, '¿Qué es la Ley de Faraday?', '{"respuestas": ["Ley que describe la inducción electromagnética", "Ley que describe la relación entre la presión y el volumen de un gas", "Ley que describe el desplazamiento de un objeto en función del tiempo", "Ley que describe la conservación de la energía mecánica"], "respuesta_correcta": "Ley que describe la inducción electromagnética"}', null),
(213, 1, '¿Qué es el Principio de Pascal?', '{"respuestas": ["La presión ejercida en un fluido se transmite de manera uniforme en todas las direcciones", "Ley que describe la relación entre la presión y el volumen de un gas", "Ley que describe el desplazamiento de un objeto en función del tiempo", "Ley que describe la conservación de la energía mecánica"], "respuesta_correcta": "La presión ejercida en un fluido se transmite de manera uniforme en todas las direcciones"}', null),
